# 위치 문자열을 파싱하는 함수
def parse_position(position_str):
    try:
        position_dict = ast.literal_eval(position_str)
        return position_dict['x'], position_dict['y']
    except (ValueError, SyntaxError, KeyError):
        return None, None

# 딕셔너리를 반전시켜 각 사각형 번호를 해당하는 지역으로 매핑합니다.
square_to_area = {square: area for area, squares in area_dict.items() for square in squares}
square_to_big_area = {square: area for area, squares in area_dict_big.items() for square in squares}
square_to_big2_area = {square: area for area, squares in area_dict_big2.items() for square in squares}

# 사각형 번호로부터 지역을 찾는 함수
def find_area(square, square_to_area):
    return square_to_area.get(square, None)

# 사각형 번호를 찾는 벡터화된 함수
def find_square_number_vectorized(positions, squares_data):
    x = positions['x'].values
    y = positions['y'].values

    square_numbers = np.full(len(x), None)

    for index, row in squares_data.iterrows():
        mask = (row['x_start'] <= x) & (x <= row['x_end']) & (row['y_start'] <= y) & (y <= row['y_end'])
        square_numbers[mask] = row['Square']

    return square_numbers


# 굴림 폰트 설정
plt.rcParams['font.family'] = 'Gulim'  # 또는 '굴림'으로도 가능

# 히트맵을 백그라운드 이미지와 함께 그리는 함수
def plot_heatmap_with_background(minute, background_image):
    column_name = f'position_{minute}'

    if column_name not in data.columns:
        raise ValueError(f"{minute}분에 대한 데이터가 없습니다")

    # 데이터 파싱
    positions = data[column_name].dropna().apply(parse_position)
    positions = pd.DataFrame(positions.tolist(), columns=['x', 'y']).dropna()

    # matchId, championName, teamcolor 컬럼 추가
    positions['Tier'] = data['Tier'].values
    positions['matchId'] = data['matchId'].values
    positions['championName'] = data['championName'].values
    positions['teamcolor'] = data['teamcolor'].values
    positions['win'] = data['win'].values

    # 벡터화된 함수로 사각형 번호 추가
    tqdm.pandas(desc="사각형 찾는 중")
    positions[f'Square_{minute}min'] = find_square_number_vectorized(positions, squares_data)

    # area_dict의 값을 매핑하여 area 컬럼 추가
    positions[f'area_{minute}min'] = positions[f'Square_{minute}min'].map(lambda sq: find_area(sq, square_to_area))
    positions[f'big_area_{minute}min'] = positions[f'Square_{minute}min'].map(lambda sq: find_area(sq, square_to_big_area))
    positions[f'big2_area_{minute}min'] = positions[f'Square_{minute}min'].map(lambda sq: find_area(sq, square_to_big2_area))

    # 위치 데이터의 첫 몇 행을 출력
    print(positions.head())

    # 위치 데이터를 CSV 파일로 저장
    file_name = f'{minute}min_position.csv'
    positions.to_csv(file_name, index=False)
    print(f'파일이 저장되었습니다: {file_name}')

    x = positions['x']
    y = positions['y']

    # 사용자 정의 컬러 맵 생성
    custom_cmap = LinearSegmentedColormap.from_list('black_to_yellow', ['black', 'yellow'])
    positions.head()

    # 플롯 생성
    fig, ax = plt.subplots(figsize=(20, 16))

    # 전체 플롯 배경색을 설정
    fig.patch.set_facecolor('#091428')

    # 백그라운드 이미지와 히트맵 그리기 (축 내부는 기본 배경색 유지)
    ax.imshow(background_image, extent=[0, 16000, 0, 16000], aspect='auto')

    # 히트맵 그리기
    hist = ax.hist2d(x, y, bins=[range(0, 16001, 500), range(0, 16001, 500)], cmap=custom_cmap, alpha=0.6)

    # Colorbar 생성 및 색상 설정
    cbar = plt.colorbar(hist[3], ax=ax, label='밀도')
    cbar.ax.yaxis.set_tick_params(color='white')
    plt.setp(plt.getp(cbar.ax, 'yticklabels'), color='white')
    cbar.set_label('밀도', color='white')

    # 그리드 설정
    ax.grid(which='both', color='gray', linestyle='--', linewidth=0.5)

    # 축과 레이블 설정 (흰색)
    ax.set_xticks(range(0, 16001, 500))
    ax.set_yticks(range(0, 16001, 500))
    ax.tick_params(axis='x', colors='white')
    ax.tick_params(axis='y', colors='white')
    ax.set_xlabel('X 위치', color='white')
    ax.set_ylabel('Y 위치', color='white')

    # 제목 설정 (흰색)
    title = f'{minute}분의 히트맵'
    ax.set_title(title, color='white')

    plt.show()

